// Menu links
function LinkList() {
  const links = [
    { to: "/", label: "Trang chính" },
    { to: "/about-fpt", label: "Về đại học FPT" },
    { to: "/about-etoad", label: "Về E-Toad" },
    { to: "/lop-hoc-ao", label: "Lớp học ảo" },
    { to: "/shop", label: "Cửa hàng" },
    { to: "/news", label: "Tin tức" },
    { to: "/account", label: "Tài khoản" },
  ];
  return (
    <div style={{ display: "flex", flexDirection: "column", gap: 12 }}>
      {links.map((l) => (
        <Link
          key={l.to}
          to={l.to}
          style={{
            fontWeight: 300,
            fontSize: 16,
            lineHeight: "22px",
            color: "#686868",
            textDecoration: "none",
          }}
        >
          {l.label}
        </Link>
      ))}
    </div>
  );
}

// Contact row
function ContactRow({ icon, text }) {
  return (
    <div style={{ display: "flex", flexDirection: "row", alignItems: "center", gap: 11, height: 25 }}>
      <div>{icon}</div>
      <div>{text}</div>
    </div>
  );
}

// Footer link row
function FooterLink({ icon, text, href }) {
  return (
    <a
      href={href}
      target="_blank"
      rel="noopener noreferrer"
      style={{
        display: "flex",
        flexDirection: "row",
        alignItems: "center",
        gap: 4,
        height: 25,
        color: "#fff",
        fontWeight: 400,
        fontSize: 16,
        lineHeight: "28px",
        textDecoration: "none",
      }}
    >      
      {icon}
      <span>{text}</span>
    </a>
  );
}

export default Footer;
